import React from 'react';
import Client from './components/Clients';
import Form from './components/Form'

import './App.css';

class App extends React.Component {

  state = {
    clients : [
      {id:1, nom:'LEE'},
      {id:2, nom:'FALL'},
      {id:3, nom:'GUEYE'},
    ]
  }
  handleChange = (event) => {
    this.setState({
      newClient : event.currentTarget.value
    })
  }
  handleAdd = (client) => {
    const clients = [...this.state.clients];
    clients.push(client)
    this.setState({
      clients,
    })
  }

render(){
    const title = "Liste des clients"
      return(
        <div className="App">
          <h1>{title}</h1>
          <ul>{this.state.clients.map(client => (
            <Client details={client} onDelete={this.handleDelete}/>
            ))}</ul>
          <Form onClientAdd={this.handleAdd}/>
        </div>
      );
    }
}

export default App;
