import React, { component } from 'react';
import './App.css';

class App extends React.Component {
  state = {
    clients : [
      {id:1, nom:'LEE'},
      {id:2, nom:'FALL'},
      {id:3, nom:'GUEYE'},
    ]
  }
  handleClick(){
    console.log("Hello")
  }
  render(){
    const title = "Liste des clients"
      return(
        <div className="App">
        <button onClick={this.handleClick}>Click Me</button>
          <h1>{title}</h1>
          <ul>{this.state.clients.map(client => (
            <li>{client.nom} <button>X</button>
            </li>
            ))}</ul>
            <form action="">
              <input type="text" placeholder="client"/> <button>Ajouter un client</button>
            </form>
        </div>
      );
    }
}

export default App;
