import React from 'react';
import Client from './components/Clients'

import './App.css';

class App extends React.Component {



  state = {
    clients : [
      {id:1, nom:'LEE'},
      {id:2, nom:'FALL'},
      {id:3, nom:'GUEYE'},
    ],
    newClient:""
  }
  handleChange = (event) => {
    this.setState({
      newClient : event.currentTarget.value
    })
  }
  handleSubmit = (event) => {
     event.preventDefault()
     const id = new Date().getTime();
     const nom = this.state.newClient;
     //const client = {id,nom};
    const clients = [...this.state.clients];
    clients.push({id,nom})
    this.setState({
      clients,
      newClient: ''
    })
  }
  handleDelete = (id) => {
    const cl = [...this.state.clients];
    const index = cl.findIndex((client)=>client.id === id)
    cl.splice(index,1)
    this.setState({
      clients:cl,
    })

  }
render(){
    const title = "Liste des clients"
      return(
        <div className="App">
          <h1>{title}</h1>
          <ul>{this.state.clients.map(client => (
            <Client details={client} onDelete={this.handleDelete}/>
            ))}</ul>
            <form onSubmit={this.handleSubmit}>
              <input value={this.state.newClient} onChange={this.handleChange} type="text" placeholder="client"/> <button>Ajouter un client</button>
            </form>
        </div>
      );
    }
}

export default App;
