import React, { Component } from 'react'

export default class Form extends Component {
    state = {
        newClient:""
    }
    handleDelete = (id) => {
        const cl = [...this.state.clients];
        const index = cl.findIndex((client)=>client.id === id)
        cl.splice(index,1)
        this.setState({
          clients:cl,
        })

      }
      handleChange = (event) => {
        this.setState({
          newClient : event.currentTarget.value
        })
      }
      handleSubmit = (event) => {
        event.preventDefault()
        const id = new Date().getTime();
        const nom = this.state.newClient;
        //const client = {id,nom};
       this.props.onClientAdd(id,nom)
       this.setState({
        newClient:""
       })
     }
    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                     <input value={this.state.newClient} onChange={this.handleChange} type="text" placeholder="client"/> 
                     <button>Ajouter un client</button>
                </form>
            </div>
        )
    }
}
