import React, { component } from 'react';
import './App.css';

class App extends React.Component {



  state = {
    clients : [
      {id:1, nom:'LEE'},
      {id:2, nom:'FALL'},
      {id:3, nom:'GUEYE'},
    ],
    newClient:"Yahya"
  }
  handleChange = (event) => {
    const value = event.currentTarget.value;
    this.setState({
      newClient : value
    })
  }
  handleSubmit = (event) => {
     event.preventDefault()
     const id = new Date().getTime();
     const nom = this.state.newClient;

    const client = {id:id , nom: nom};

    const clients = this.state.clients.slice();
    clients.push(client)
    this.setState({
      clients : clients,
      newClient: ''
    })
  }
  handleDelete = (id) => {
    const cl = this.state.clients.slice();
    const index = cl.findIndex((client)=>client.id === id)
    cl.splice(index,1)
    this.setState({
      clients:cl,
    })

  }
render(){
    const title = "Liste des clients"
      return(
        <div className="App">
          <h1>{title}</h1>
          <ul>{this.state.clients.map(client => (
            <li>{client.nom} <button onClick={()=>this.handleDelete(client.id)}>X</button>
            </li>
            ))}</ul>
            <form onSubmit={this.handleSubmit}>
              <input value={this.state.newClient} onChange={this.handleChange} type="text" placeholder="client"/> <button>Ajouter un client</button>
            </form>
        </div>
      );
    }
}

export default App;
