import React, { component } from 'react';
import './App.css';

class App extends React.Component {

  reactInput = React.createRef()

  state = {
    clients : [
      {id:1, nom:'LEE'},
      {id:2, nom:'FALL'},
      {id:3, nom:'GUEYE'},
    ]
  }
  handleSubmit = (e) => {
     e.preventDefault()
     console.log(this.reactInput.current.value)
  }
  handleDelete = (id) => {
    const cl = this.state.clients.slice();
    const index = cl.findIndex((client)=>client.id === id)
    cl.splice(index,1)
    this.setState({
      clients:cl,
    })

  }
render(){
    const title = "Liste des clients"
      return(
        <div className="App">
          <h1>{title}</h1>
          <ul>{this.state.clients.map(client => (
            <li>{client.nom} <button onClick={()=>this.handleDelete(client.id)}>X</button>
            </li>
            ))}</ul>
            <form onSubmit={this.handleSubmit}>
              <input refs={this.reactInput} type="text" placeholder="client"/> <button>Ajouter un client</button>
            </form>
        </div>
      );
    }
}

export default App;
