import React, { component } from 'react';
import './App.css';

class App extends React.Component {
  state = {
    clients : [
      {id:1, nom:'LEE'},
      {id:2, nom:'FALL'},
      {id:3, nom:'GUEYE'},
    ]
  }
  // handleDelete = (id) => {
