  //   const index = clients.findIndex(function(){
  //     return(index, 1)
  //   })
  //   index.s
  // }
handleClick = () => {
  const cl = this.state.clients.slice();
  cl.push({id:4,nom:"Barmaki"})
  this.setState({clients:cl})
}
  render(){
    const title = "Liste des clients"
      return(
        <div className="App">
          <button onClick={this.handleClick}>Click me</button>
          <h1>{title}</h1>
          <ul>{this.state.clients.map(client => (
            <li>{client.nom} <button onClick={()=>this.handleDelete(client.id)}>X</button>
            </li>
            ))}</ul>
            <form action="">
              <input type="text" placeholder="client"/> <button>Ajouter un client</button>
            </form>
        </div>
      );
    }
}

export default App;
